$k = '3ddf0d5c';  // å¯†é’¥

function x($t, $k)
{
    $c = strlen($k);  // å¯†é’¥é•¿åº¦
    $l = strlen($t);  // æ–‡æœ¬é•¿åº¦
    $o = '';

    // XOR åŠ å¯†/è§£å¯†
